import React, { useEffect, useState } from "react";
import "../css/adminDashBoard.css";
import { Function } from "../config/function";
import { supabase } from "../supabasePrivate";
import Swal from "sweetalert2";
export default function Admindashboard() {
    const [userCount, setUserCount] = useState(null);
    const [productCount, setProductCount] = useState(null);
    const [products, setProducts] = useState();
    const loadDashboardData = async () => {
        try {
            const { count: uCount, error: userError } = await supabase
                .from("user")
                .select("*", { count: "exact", head: true });
            if (userError) throw userError;
            setUserCount(uCount || 0);
            const { data: pData, count: pCount, error: productError } = await supabase
                .from("product1")
                .select("*", { count: "exact" });
            if (productError) throw productError;

            setProductCount(pCount || 0);
            setProducts(pData || []);
        } catch (err) {
            console.error("L·ªói khi t·∫£i d·ªØ li·ªáu dashboard:", err);
        }
    };

    const addProduct = async () => {

        Swal.fire({
            title: "Th√™m s·∫£n ph·∫©m m·ªõi",
            html: `
    <input id="productName" class="swal2-input" placeholder="T√™n s·∫£n ph·∫©m">
    <input id="productPrice" type="number" class="swal2-input" placeholder="Gi√° ti·ªÅn" type="number" min="0">
    <input id="productImage" class="swal2-input" placeholder="ƒê∆∞·ªùng d·∫´n ·∫£nh">
  `,
            showCancelButton: true,
            confirmButtonText: "Th√™m",
            cancelButtonText: "H·ªßy",
            focusConfirm: false,
            preConfirm: () => {
                const name = document.getElementById("productName").value.trim();
                const price = document.getElementById("productPrice").value.trim();
                const image = document.getElementById("productImage").value.trim();

                if (!name || !price || !image) {
                    Swal.showValidationMessage("Vui l√≤ng nh·∫≠p ƒë·∫ßy ƒë·ªß th√¥ng tin s·∫£n ph·∫©m!");
                    return false;
                }

                return { name, price, image };
            },
        }).then((result) => {
            if (result.isConfirmed) {
                const { name, price, image } = result.value;
                Swal.fire({
                    title: "S·∫£n ph·∫©m ƒë√£ th√™m",
                    html: `
        <p><strong>T√™n:</strong> ${name}</p>
        <p><strong>Gi√°:</strong> ${price} VNƒê</p>
        <img src="${image}" alt="H√¨nh ·∫£nh s·∫£n ph·∫©m" style="max-width:200px; margin-top:10px; border-radius:10px;">
      `,
                    icon: "success",
                });

                // üëâ B·∫°n c√≥ th·ªÉ g·ªçi API ho·∫∑c x·ª≠ l√Ω l∆∞u d·ªØ li·ªáu ·ªü ƒë√¢y
                console.log("D·ªØ li·ªáu s·∫£n ph·∫©m:", { name, price, image });
            }
        });
    }

    const deleteProduct = async (id) => {
        if (!Function.isAdmin()) {
            window.location.href = Function.base_Url();
            return;
        }
        const confirmDelete = window.confirm("B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a s·∫£n ph·∫©m n√†y?");
        if (!confirmDelete) return;

        const { error } = await supabase.from("product1").delete().eq("id", id);
        if (error) {
            Function.makeToast("X√≥a th·∫•t b·∫°i!", "error");
        } else {
            Function.makeToast("ƒê√£ x√≥a s·∫£n ph·∫©m!", "success");
            setProducts((prev) => prev.filter((p) => p.id !== id));
            loadDashboardData();
        }
    };

    useEffect(() => {
        if (!Function.isAdmin()) {
            window.location.href = Function.base_Url();
            return;
        }
        loadDashboardData();
    }, []);

    return (
        <div className="dashboard-page">
            <div className="top-bar">
                <button
                    className="home-btn"
                    onClick={() => (window.location.href = Function.base_Url())}
                >
                    ‚Üê Trang ch·ªß
                </button>
            </div>

            {/* ========== HEADER CARDS ========== */}
            <div className="dashboard-container">
                <div className="card">
                    <div className="icon-wrapper">
                        <i className="fa fa-users"></i>
                    </div>
                    <div className="info">
                        <span className="label">Th√†nh vi√™n</span>
                        <h2 className="value">
                            {userCount !== null ? userCount : "ƒêang ch·ªù..."}
                        </h2>
                    </div>
                    <div className="change positive">
                        <i className="fa fa-arrow-up"></i> 11.01%
                    </div>
                </div>

                <div className="card">
                    <div className="icon-wrapper">
                        <i className="fa fa-box"></i>
                    </div>
                    <div className="info">
                        <span className="label">S·∫£n ph·∫©m</span>
                        <h2 className="value">
                            {productCount !== null ? productCount : "ƒêang ch·ªù..."}
                        </h2>
                    </div>
                    <div className="change positive">
                        <i className="fa fa-arrow-up"></i> 8.34%
                    </div>
                </div>
            </div>

            {/* ========== PRODUCT TABLE ========== */}
            <div className="table-container">
                <div className="table-header">
                    <h2 className="table-title">Danh s√°ch s·∫£n ph·∫©m</h2>
                    <button className="add-btn" onClick={() => addProduct()}>
                        + Th√™m
                    </button>
                </div>
                <hr></hr>
                <table className="product-table">
                    <thead>
                        <tr>
                            <th>Stt</th>
                            <th style={{ width: "40%" }}>T√™n s·∫£n ph·∫©m</th>
                            <th style={{ width: "10%" }}>Gi√°</th>
                            <th style={{ width: "30%" }}>·∫¢nh</th>
                            <th style={{ width: "60%" }}>Ch·ª©c nƒÉng</th>
                        </tr>
                    </thead>
                    <tbody>
                        {Array.isArray(products) && products.length > 0 ? (
                            products.map((p, i) => (
                                <tr key={p.id}>
                                    <td>{i++}</td>
                                    <td>{p.title}</td>
                                    <td>{p.price.toLocaleString("vi-VN")}‚Ç´</td>
                                    <td><img src={p.image} style={{ width: "20%" }} alt="" srcset="" /></td>
                                    <td><button type="button"
                                        onClick={() => deleteProduct(p.id)}
                                        style={{ color: "#ffff", backgroundColor: "#dc3545", border: "none", borderRadius: "15px", width: "50px", height: "30px" }}
                                    >X√≥a</button>
                                    </td>

                                </tr>
                            ))
                        ) : (
                            <tr>
                                <td colSpan={5} style={{ textAlign: "center" }}>
                                    ƒêang t·∫£i s·∫£n ph·∫©m...
                                </td>
                            </tr>
                        )}
                    </tbody>

                </table>
            </div>
        </div>
    );
}
